mapper.insert:
UPDATE board
SET re_step = re_step + 1
WHERE ref = #{ref} and re_step >= #{re_step}

mapper.select:
SELECT filename
FROM board
WHERE num = #{num}

mapper.update:
UPDATE board
SET down = down + 1
WHERE num = #{num}

UPDATE board
SET readcount = readcount+1
WHERE num = #{num}

mapper.select:
SELECT passwd
FROM board
WHERE num = #{num} and passwd = #{passwd}
